import React, { useContext } from "react";
import { TodoContex } from "../contex/todo_contex";
import SingleListItem from "./singleListItem";

const TodoList = () => {
    const {todos,dispatch} = useContext(TodoContex)

    const handleDone = ({id}) => {
        dispatch({
            // todos.map((item) => {
            //     if(item.id === todo.id){
            //         return {...item, completed: !item.completed}
            //     }
            //     return item
            // })
            type : "complete",
            // id: id,
            // todo: todos 
            

        })
    }

    const handleDelete = ({id}) => {
        dispatch({
            type: "delete",
            // id: id,
                // todos.filter((todo) => todo.id !== id)
        })
    }

    return (
            <ul>
                {todos.map((todo) => (
                    // return <SingleListItem key={todo.id} todo = {todo} setTodos={setTodos} todos = {todos}/>;
                    <li
                        className="list-item"
                        key={todo.id}
                    >
                        <div
                            className={`list ${todo.completed ? "complete" : ""}`}
                        >
                            {todo.text}
                        </div>
                        <div
                            className= "icons"
                        >
                            <button className="button-complete" onClick={() => handleDone(todo)}>
                                <i className="fa-solid fa-circle-check"></i>
                            </button>
                            <button className="button-delete" onClick={() => handleDelete(todo)}>
                                <i className="fa-solid fa-trash"></i>
                            </button>
                        </div>
                    </li>
                ))}
            </ul> 
        
    );
};

export default TodoList
