import React from "react"

const  SingleListItem = ({key,setTodos,todos}) => {
    const title = key.title
    const handleDone = (todo) => {
        setTodos(
            todos.map((item) => {
                if(item.id === todo.id){
                    return {...item, completed: !item.completed}
                }
                return item
            })
        )
    }

    const handleDelete = ({id}) => {
        setTodos(todos.filter((todo) => todo.id !== id))
    }

    return(
        <li
            className="list-item"
            key={key.id}
        >
            <div
                className={`list ${key.completed ? "complete" : ""}`}
            >
                {title}
            </div>
            <div
                className= "icons"
            >
                <button className="button-complete" onClick={() => handleDone(key)}>
                    <i className="fa-solid fa-circle-check"></i>
                </button>
                <button className="button-delete" onClick={() => handleDelete(key)}>
                    <i className="fa-solid fa-trash"></i>
                </button>
            </div>
        </li>
    );
}

export default SingleListItem
